import React , {Component} from 'react';

import gql from 'apollo-boost';
import {graphql} from 'react-apollo';
 
 const fetchResult = gql`
 {
     books{
         name
         id
     }
 }
 `
class BookList extends Component{
    displayBooks(){
        const data = this.props.fetchBooks;
        if(data.loading){
            return (
                <div>Loading Books......</div>
            )
        }
        else{
         console.log(data)
        }

    }
 render(){
     return(
         <div>
             <ul>
                 <li>{this.displayBooks()}</li>
             </ul>
         </div>
     )
 }
}

export default graphql(fetchResult, {
    name:"fetchBooks"
})(BookList)
