import React,{Component} from 'react';

import {Card , Col, Row, CardBody, CardGroup,CardTitle,Container, CardSubtitle, Button} from 'reactstrap';
import { CartContext } from './CartContext';

class CartItem extends Component{

render(){
  
    return(
       <CartContext.Consumer>
           {({cart})=>{
               return (
                  
                <Row>
                    <Col sm={12} md={6}>
                   <span>
                       {cart.map((item)=>{
                           console.log(cart)
                          return (
                             
                                  
                             <Card>
                               <CardGroup>
                                   <CardBody>
                                       <CardTitle>{item.item.name}</CardTitle>
                                       <CardSubtitle>Description {item.item.description}</CardSubtitle>
                                       <CardSubtitle>Item Quantity {item.item.qty}</CardSubtitle>
                                       <CardSubtitle>Item Price {item.item.price}</CardSubtitle>
                                       <Row>
                                           <Col sm={12} md={6}>
                                           <Button color="primary">Increase quantity by one</Button>

                                           </Col>
                                           <Col sm={12} md={6}>
                                           <Button color="danger">Reduce quantity by one</Button>
                                           </Col>
                                       </Row>
                                   </CardBody>
                               </CardGroup>

                             </Card>
                                  
                              
                            
                          )
                       })}
                   </span>
                   </Col>
                   </Row>
                   
                  
               )
           }}
       </CartContext.Consumer>
    )
}
}

export default CartItem;
