import React, {useState, createContext} from 'react';

const CartContext= createContext();

const Cart=(props)=>{
    const [cart, setCart] = useState([]);
    const set_new_cart= (cartitems)=>{
      
       setCart(cartitems);

    }
    return (
        <CartContext.Provider value= {{...props, set_new_cart , cart}}>
            {props.children}
        </CartContext.Provider>
    )
    
}
export {CartContext};
export default Cart
