import React, {Component} from 'react';
import {gql} from 'apollo-boost';
import {graphql} from 'react-apollo';
const getAuthorQuery = gql`
    {
       authors{
           name
           id
       } 
    }

`
class addBook extends Component{
    displayAuthors(){
        var data= this.props.data
        if(data.loading){
            return(
                <option disabled>Loading authors.....</option>
            )   
        }
        else{
            return data.authors.map(author=>{
                
             return <option>{author.name}</option>
            })
        }
    }
    render(){
        return(
            <form id='add-book'>
                <div className='field'>
                    <label>Name</label>
                    <input type='text' placeholder='enter name'/>
                </div>

                <div className='field'>
                    <label>Genre</label>
                    <input type='text' placeholder='enter genre'/>
                </div>
                <div className='field'>
                    <label>Author</label>
                   <select>
                       {this.displayAuthors()}
                   </select>
                </div>
                <div className='field'>
                   <button>+</button>
                </div>

            </form>
        )
    }
}


export default graphql(getAuthorQuery)(addBook);
