import React, {Component} from 'react';

import {graphql,wi} from 'react-apollo';
import {getAuthorQuery, addBookMutation} from '../queries/queries';
class addBook extends Component{
    constructor(props){
        super(props)
        this.state={
            name:"",
            genre:"",
            authorId:""
        }
    }
    displayAuthors(){
        var data= this.props.data
        if(data.loading){
            return(
                <option disabled>Loading authors.....</option>
            )   
        }
        else{
            return data.authors.map(author=>{
                
             return <option key={author.id} value={author.id}>{author.name}</option>
            })
        }
    }
    submitForm(e){
     e.preventDefault();
     console.log(this.state)
    }
    render(){
        return(
            <form onSubmit={this.submitForm.bind(this)} id='add-book'>
                <div className='field'>
                    <label>Name</label>
                    <input onChange={(e)=>{this.setState({name:e.target.value})}} type='text' placeholder='enter name'/>
                </div>

                <div className='field'>
                    <label>Genre</label>
                    <input  onChange={(e)=>{this.setState({genre:e.target.value})}} type='text' placeholder='enter genre'/>
                </div>
                <div className='field'>
                    <label>Author</label>
                   <select  onChange={(e)=>{this.setState({authorId:e.target.value})}}>
                       {this.displayAuthors()}
                   </select>
                </div>
                <div className='field'>
                   <button>+</button>
                </div>

            </form>
        )
    }
}


export default withApollo(
    graphql(getAuthorQuery,{name:"getAuthorQuery"}),
    graphql(addBookMutation,{name:"addBookMutation"})
)(addBook)
