import React , {Component} from 'react';

import {gql} from 'apollo-boost';
import {graphql} from 'react-apollo';
import BookDetails from './BookDetails';

const getBookQuery = gql`
    {
       books{
           name
           id
       } 
    }

`

class BookList extends Component{
    constructor(props){
        super(props);
        this.state={
            selected:null,
            id:"",
            delete:
            gql`
 mutation($id:ID!){
     deleteBook(id:$id){
         id
     }
 }


`
        }
    }
    delete(){
        this.deleteBook({
            variables:{
                id:this.state.selected
            },
            refetchQueries:[{query:getBookQuery}]
        })
    }
  displayBooks(){
      const data= this.props.getBookQuery;
      if(data.loading){
          return(
              <div>Loading Books...</div>
          )
      }
      else{
          return data.books.map((book)=>{
             return (
                 <li key={book.id} onClick={(e)=>this.setState({selected:book.id})} value={book.id}>{book.name}
                 <span key={book.id} onClick={this.delete()}>Delete</span>
                 </li>
             )
          })
      }
  }
 render(){
     
     return(
         <div id="book-list">
             <ul>
                 {this.displayBooks()}
             </ul>
             <BookDetails bookid={this.state.selected}/>
         </div>
     )
 }
}

export default graphql(getBookQuery, {
    name:"getBookQuery"
})(BookList)
