import React , {Component} from 'react';
import {Row, Col , Button} from 'reactstrap';
import AddToCartForm from './components.js/Addtocart';
import CartContext from './components.js/CartContext';
import CartItem from './components.js/CartItems';

class App extends Component {
  render(){
    return (

      <div className="App">

      <CartContext>
        <Row>
          <Col sm={12} md={6} >
          <AddToCartForm/>
          </Col>
          <Col sm={12} md={6} >
            <h1>Items in cart</h1>
          <CartItem/>
          <Button size={20} color="primary">Delete all items in your cart</Button>
          </Col>
        </Row>




      </CartContext>   

      </div>


    );
  }

}

export default App;
