import React,{Component , Consumer} from 'react';

import {Card , Col, Row, CardBody, CardGroup,CardTitle, CardSubtitle, Button} from 'reactstrap';
import Cart, { CartContext } from './CartContext';

class CartItem extends Component{
constructor(props){
    super(props);
    
}
render(){
  
    return(
       <CartContext.Consumer>
           {({cart})=>{
               return (
                <Row>
                   <ul>
                       {cart.map((item)=>{
                          return (
                             
                                  <Col sm={12} md={6}>
                             <Card>
                               <CardGroup>
                                   <CardBody>
                                       <CardTitle>{item.item.name}</CardTitle>
                                       <CardSubtitle>Description {item.item.description}</CardSubtitle>
                                       <CardSubtitle>Item Quantity {item.item.qty}</CardSubtitle>
                                       <CardSubtitle>Item Price {item.item.price}</CardSubtitle>
                                       <Row>
                                           <Col>
                                           <Button color="primary">Increase quantity by one</Button>
                                           </Col>
                                           <Col>
                                           </Col>
                                       </Row>
                                   </CardBody>
                               </CardGroup>

                             </Card>
                                  </Col>
                              
                            
                          )
                       })}
                   </ul>
                   </Row>
                  
               )
           }}
       </CartContext.Consumer>
    )
}
}

export default CartItem;
