import React, {Component} from 'react';
import {gql} from 'apollo-boost';
import {graphql} from 'react-apollo';
const getAuthorQuery = gql`
    {
       authors{
           name
           id
       } 
    }

`
class addBook extends Component{
    displayData(){
        var data= this.props.data
        if(data.loading){
            return(
                <div><h1></h1></div>
            )
        }
    }
    render(){
        return(
            <form id='add-book'>
                <div className='field'>
                    <label>Name</label>
                    <input type='text' placeholder='enter name'/>
                </div>

                <div className='field'>
                    <label>Genre</label>
                    <input type='text' placeholder='enter genre'/>
                </div>
                <div className='field'>
                    <label>Author</label>
                   <select>
                       <option>Select Author</option>
                   </select>
                </div>
                <div className='field'>
                   <button>+</button>
                </div>

            </form>
        )
    }
}


export default graphql(getAuthorQuery)(addBook);
