import React,{Component} from 'react';

import {Card , Col, Row,Badge, CardBody, CardGroup,CardTitle,Container, CardSubtitle, Button} from 'reactstrap';
import { CartContext } from './CartContext';

class CartItem extends Component{
async reduceByOne(id , set_new_cart){
    
  await fetch('http://localhost:4000/reducebyone', {
   method:"post",
   headers:{
       "content-type":'application/json'
   },
   body: JSON.stringify({
       id
   })
  }).then((res)=>{
      return res.json()
  })
  .then((resJson)=>{
    set_new_cart(resJson)

  })
  .catch((err)=>{
      console.error(err)
  })
}

render(){
  
    return(
       <CartContext.Consumer>
           {(context)=>{
               const {cart, set_new_cart} = context;
               return (
                  
                <Row>
                   
                       {cart.map((item)=>{
                          return (
                             
                                  <Col key={item.item.id} sm={12} md={5}>
                             <Card >
                               <CardGroup>
                                   <CardBody>
                                       <CardTitle color="black">Name <Badge className="badge" color="success">{item.item.name}</Badge></CardTitle>
                                      <hr/>
                                       <CardSubtitle>Item Quantity <Badge className="badge" color="success">{item.qty}</Badge> </CardSubtitle>
                                       <hr/>
                                       <CardSubtitle>Item Price  <Badge className="badge" color="success">{item.price}</Badge></CardSubtitle>
                                       <hr/>
                                       <Row>
                                           <Col sm={12} md={6}>
                                           <Button  color="primary">Increase by one +</Button>

                                           </Col>
                                           <Col sm={12} md={6}>
                                           <Button onClick={()=> this.reduceByOne(item.item.id , set_new_cart)} color="danger">Reduce by one one -</Button>
                                           </Col>
                                       </Row>
                                   </CardBody>
                               </CardGroup>

                             </Card>
                                  </Col>
                              
                            
                          )
                       })}
                   
                   </Row>
                   
                  
               )
           }}
       </CartContext.Consumer>
    )
}
}

export default CartItem;
