import React, {Component} from 'react';

import {graphql} from 'react-apollo';
import {getAuthorQuery} from '../queries/queries';
class addBook extends Component{
    constructor(props){
        super(props)
        this.state={
            name:"",
            genre:"",
            authorId:""
        }
    }
    displayAuthors(){
        var data= this.props.data
        if(data.loading){
            return(
                <option disabled>Loading authors.....</option>
            )   
        }
        else{
            return data.authors.map(author=>{
                
             return <option key={author.id} value={author.id}>{author.name}</option>
            })
        }
    }
    render(){
        return(
            <form id='add-book'>
                <div className='field'>
                    <label>Name</label>
                    <input onChange={(e)=>{this.set}} type='text' placeholder='enter name'/>
                </div>

                <div className='field'>
                    <label>Genre</label>
                    <input type='text' placeholder='enter genre'/>
                </div>
                <div className='field'>
                    <label>Author</label>
                   <select>
                       {this.displayAuthors()}
                   </select>
                </div>
                <div className='field'>
                   <button>+</button>
                </div>

            </form>
        )
    }
}


export default graphql(getAuthorQuery)(addBook);
