const reducer= (state, action)=>{
    var id=0;
    switch(action.type){
       
        case "ADD_BOOK":
            return  [...state, {
                title:action.book.title,
                author: action.book.author,
                id:id++
            }]

        case "REMOVE_BOOK":
            return  state.filter((book)=> book.id !== action.id)
                   
    }
}
export default reducer
