import React , {Component} from 'react';

import {gql} from 'apollo-boost';
import {graphql} from 'react-apollo';
 const authors= [
    {
      "name": "ADEWUMI SUNKANMI",
      "id": "5d6d334831bc5010f8816348"
    },
    {
      "name": "Benne hinn",
      "id": "5d6d33c331bc5010f881634a"
    },
    {
      "name": "Andrew Wommack",
      "id": "5d6d344231bc5010f881634c"
    }
  ]

// const addBookQuery = gql`
//     mutation(){
//        addBook(){
//            name
//            id
//        } 
//     }

// `
class Ad extends Component{
  displayAuthors(){
      const data= this.props.data;
      if(data.loading){
          return(
              <div>Loading Authors......</div>
          )
      }
      else{
          return authors.map((author)=>{
             return (
                 <option key={author.name} value={author.id}>{author.name}</option>
             )
          })
      }
  }
 render(){
     
     return(
         <div>
             <form id="form-data">
                <div className="field">
                    <label>Name</label>
                 <input type="text"/>
                </div>
                <div className="field">
                    <label>Genre</label>
                 <input type="text"/>
                </div>
                <div className="field">
                    <label>Select Author</label>
                 <select>
                     {this.displayAuthors()}
                 </select>
                </div>
             </form>
         </div>
     )
 }
}

export default  addB
