import React , {Component} from 'react';
import {ListGroup, ListGroupItem,ListGroupItemHeading, Button,Form,Alert, FormGroup, InputGroup, Row,Badge, Col, ButtonDropdown, ListGroupItemText} from 'reactstrap'

class Chat extends Component {
  state={
    selected:false,
    message_to:"Dangote company official discussion group"
  }
   Submit=(e)=>{
    e.preventDefault();
  
     
  }

   renderForm=()=>{
     if(!this.state.selected){
      return (
        <div>
          
        <div id="chat-section">
        
        <Col id="chat-col" sm={12} md={12}>
        <h5 id="head" >Send message to {this.state.message_to}  </h5><hr/>
          <p id="date">September 29, 2019</p>
          <div id="chat-container">

          </div>
       
     
         </Col>
        
        </div>
        <Col id="chat-col" sm={12} md={12}> 
        <Form  id="form" >
         
       
         <FormGroup>
           <p style={{fontSize:"13px"}}>Message {this.state.message_to}</p>
           <InputGroup>
        
           <input  name="sunkanmi" placeholder="Enter mesage" id="chat-message"/>
           </InputGroup>
         </FormGroup>
         
         <FormGroup>
        
           <Button id="shout" color="primary"  type="submit">Send </Button>
         </FormGroup>
        
       </Form>
        </Col>
       
      </div>
      )
     }
     return (
      <FormGroup>
             <Alert id="alert">
     
     Please select who chat with in friend list or </Alert>
      <Button id="shout" color="primary" onClick={()=>{
        this.setState({selected:true , message_to:"Dangote company official discussion group"})
      }}  type="button">Join dangote group discussion</Button>
    </FormGroup>
     )
     
    
  }

  render(){

   
    return (
     
     <div>

  <div className="chat-div">

   <Row>
     <Col md={3} sm={12}>
     <div id="sidebar">
    <h4>Dangote firm</h4>
    <h6> Employees</h6>
    <hr/>
    
    <ul>
    <ListGroup onClick={()=>{
     
     this.setState({selected:true});
   
     
   }}>
       <ListGroupItem style={{height:'55px'}}
       onClick={()=>{
         this.setState({selected:true , message_to:"Dangote official discussion group"})
       }}  id="list-item">
         <ListGroupItemHeading id="list-header">Dangote official disccusion group</ListGroupItemHeading>
        
       </ListGroupItem>
     </ListGroup>
      
    <ListGroup onClick={()=>{
     
      this.setState({selected:true});
    
      
    }}>
        <ListGroupItem 
        onClick={()=>{
          this.setState({selected:true , message_to:"Mr Ojomolade Seun"})
        }}  id="list-item">
          <ListGroupItemHeading id="list-header">General manager</ListGroupItemHeading>
          <ListGroupItemText id="list-text">Mr Ojomolade Seun<span id="online"></span></ListGroupItemText>
        </ListGroupItem>
      </ListGroup>
      
      <ListGroup onClick={()=>{
      this.setState({selected:true , message_to:"Mr Ojomolade Sina"})
     this.setState({selected:true});
   
     
   }}>
       <ListGroupItem id="list-item">
         <ListGroupItemHeading id="list-header">Assistant general Manager</ListGroupItemHeading>
         <ListGroupItemText id="list-text">Mr Ojomolade Seun <i>is typing</i><span id="online"></span></ListGroupItemText>
       </ListGroupItem>
     </ListGroup>
     <ListGroup onClick={()=>{
     
     this.setState({selected:true});
     this.setState({selected:true , message_to:"Mr Bomojo Kehinde"})
     
   }}>
       <ListGroupItem id="list-item">
         <ListGroupItemHeading id="list-header">Secretary</ListGroupItemHeading>
         <ListGroupItemText id="list-text">Mr Bomojo Kehinde<span id="online"></span></ListGroupItemText>
       </ListGroupItem>
     </ListGroup>
     <ListGroup onClick={()=>{
     
     this.setState({selected:true});
     this.setState({selected:true , message_to:"Mr Sarumi Gowon"})
     
   }}>
       <ListGroupItem id="list-item">
         <ListGroupItemHeading id="list-header">Assistant general secretary</ListGroupItemHeading>
         <ListGroupItemText id="list-text">Mr Sarumi gowon<span id="online"></span></ListGroupItemText>
       </ListGroupItem>
     </ListGroup>
     <ListGroup onClick={()=>{
     
     this.setState({selected:true});
   
     
   }}>
       <ListGroupItem id="list-item">
         <ListGroupItemHeading id="list-header">Financial secretary</ListGroupItemHeading>
         <ListGroupItemText id="list-text">Mrs Fabanwo Yomade<span id="online"></span></ListGroupItemText>
       </ListGroupItem>
     </ListGroup>
     <ListGroup onClick={()=>{
     
     this.setState({selected:true});
   
     
   }}>
       <ListGroupItem id="list-item">
         <ListGroupItemHeading id="list-header">Treasurer</ListGroupItemHeading>
         <ListGroupItemText id="list-text">Mrs Johnson Love<span id="online"></span></ListGroupItemText>
       </ListGroupItem>
     </ListGroup>
     <ListGroup onClick={()=>{
     
     this.setState({selected:true});
   
     
   }}>
       <ListGroupItem id="list-item">
         <ListGroupItemHeading id="list-header">Cheif Technical officer</ListGroupItemHeading>
         <ListGroupItemText id="list-text">Mr Adewumi Sunkanmi<span id="online"></span></ListGroupItemText>
       </ListGroupItem>
     </ListGroup>
    </ul>
  </div>
     </Col>
    <Col sm={12} md={9}>
      <ButtonDropdown id="buttons">
       
    
        <Button id="chat" >Unread messages<Badge id="badge" color="primary">10</Badge></Button>
        <Button  id="room">View profile</Button>
      </ButtonDropdown>

      {
          this.renderForm()
        }
          
      
    </Col>

    </Row>    
 </div>

   
 </div>
      
    );
  }
 
}

export default Chat;
