import React, {useState, useEffect} from 'react'
import {Bar , Line, Pie } from '../../node_modules/react-chartjs-2'
const Graph = () => {
    const [data, setData]= useState({});
  useEffect(()=>{
      setData({
        labels:[
            'January',
            'February',
            'March',
            'April',
            'May',
            'June',
            'July',
            'August',
            'September',
            'October',
            'November',
            'December'
        ],
        datasets:[
            {
                label:"Business Growth Rate",
                data:[
                    ,
                    836641,
                    256172,
                    152612,
                    212543,
                    270000,
                    533000,
                    900000,
                    254343,
                    152234,
                    812908,
                    471208,

                ],
                backgroundColor:[
                    "royalblue",
                    'red',
                    'pink',
                    'orange',
                    'yellow',
                    'brown',
                    'deeppink',
                    'skyblue',
                    'cyan',
                    'blue',
                    'springgreen',
                    'purple'

                ]
            }

        ]


      })
  })
    return (
        <div>
            <Bar
            data={data}
            options={{
               maintainAspectRatio:true,
               title:{
                   display:true,
                   text:"The growth rate of your business",
                   fontSize:'14'
               },
               legend:{
                   display:true,
                   position:'top',
                
               }

            }}
            />

        

            
        </div>
    )
}

export default Graph
