import React , {Component} from 'react';

import {gql} from 'apollo-boost';
import {graphql} from 'react-apollo';
 const authors= [
    {
      "name": "ADEWUMI SUNKANMI",
      "id": "5d6d334831bc5010f8816348"
    },
    {
      "name": "Benne hinn",
      "id": "5d6d33c331bc5010f881634a"
    },
    {
      "name": "Andrew Wommack",
      "id": "5d6d344231bc5010f881634c"
    }
  ]

const addBookQuery = gql`
  mutation($name:String!, $genre:String!, $authorId:ID!){
      addBook(name:$name, genre:$genre,authorId:$authorId){
          name
      }
  }

`
const getBooksQuery = gql`
    {
       books{
           name
           id
       } 
    }

`
class AddBook extends Component{
    constructor(props){
     super(props);
     this.state={
         name:"",
         genre:"",
         authorId:""
     }
    }
  displayAuthors(){
      const data= this.props.addBook;
      if(data.loading){
          return(
              <option>Loading Authors......</option>
          )
      }
      else{
          return authors.map((author)=>{
             return (
                 <option key={author.name} value={author.id}>{author.name}</option>
             )
          })
      }
  }
  submit(e){
    e.preventDefault();
    console.log(this.state)
    // this.props.addBook({
    //     variables:{
    //         name:this.state.name,
    //         genre:this.state.genre,
    //         authorId:this.state.authorId
    //     },
    //     refetchQueries:[{query:getBooksQuery}]
    // })
   
  }
 render(){
     
     return(
         <div>
             <form onSubmit={this.submit.bind(this)} id="form-data">
                <div className="field">
                    <label>Name</label>
                 <input onClick={(e)=>{this.setState({name:e.target.value})}} type="text"/>
                </div>
                <div className="field">
                    <label>Genre</label>
                 <input onClick={(e)=>{this.setState({genre:e.target.value})}} type="text"/>
                </div>
                <div className="field">
                    <label>Select Author</label>
                 <select onClick={(e)=>{this.setState({name:e.target.value})}}>
                     {this.displayAuthors()}
                 </select>
                </div>
                <div className="field">
                 <button>+</button>
                </div>
             </form>
         </div>
     )
 }
}

export default graphql(addBookQuery, {
    name:"addBook"
})(AddBook)
