import React, {useState} from 'react';


const AddToCartForm= (props)=>{
const [name, setName]= useState('');
const [price, setPrice] = useState(0);
const [description , setDescription]= useState("")
const [id, setId]= useState(null);

function submit(e){
e.preventDefault();

}

return(

    <form class="form" onSubmit={(e)=>submit(e)}>
<h2>Add item to cart</h2>
<div className="form-group">
    <label>Name</label>
<input  type="text" onChange={(e)=>setName(e.target.value)} name="name"  placeholder="Enter name"/>
</div>
<div className="form-group">
<label>Description</label>
<input type="text"  onChange={(e)=>setDescription(e.target.value)} name="des"  placeholder="description"/>
</div>

<div className="form-group">
<label>Price</label>
<input type="number"  onChange={(e)=>setName(e.target.value)} name="price"  placeholder="Price"/>


</div>

<div className="form-group">
<label>Id</label>
<input type="number" name="id" placeholder="unique id" />
</div>

<div className="form-group">
<button type="submit">Add item to cart +</button>
</div>
</form>
)




}

export default AddToCartForm
