import React from 'react'
const user= localStorage.getItem('user');
const userJSON= JSON.parse(user)
const pro= userJSON.products
const Products = () => {
    fetch("http://localhost:3003/getProducts", {
        method:'get',
        headers:{
         "content-type":'application/json'
        },
        body:JSON.stringify({
           name,
           description,
           price,
           user: UserJSON._id
          
        })
     }
     )
     .then((res)=>{
         return res.json()
     })
    return (
        <React.Fragment>
              <div className="col-10 col-md-6 mx-auto">
                  <h3 className="text-center text-slant">Your products</h3>
                {pro?
                <div className="row">
                    {
               userJSON.products.map((product)=>{
               return(
       
             <div className="col-10 col-md-4 mx-auto my-4">
            <div className="card product">
            <div className="card-title">
            <h5>{product.name}</h5>
            </div>
            <div className="card-body">
            {product.description}

            </div>
            <div className="price badge bg-primary">
            â‚¦{product.price}
            </div>

        </div>
      
    
            </div>
       
                             )
  
                         })
                    }
                   </div>:
                    <h2>You have not uploaded any product</h2>
                }
                
               </div>
               
            
         
        </React.Fragment>
    )
}

export default Products
