import React, {Suspense, Profiler} from 'react';

import {Spinner} from 'reactstrap'
const OtherComponent = React.lazy(() => import('./Dashboard'));
const AnotherComponent = React.lazy(() => import('./Nav'));

function MyComponent(props) {
    
  return (
    <div>

        
      <Suspense fallback={<div ><Spinner/></div>}>
        <section>
        {/* <Profiler id="OtherComponent" onRender={(id,phase, actualDuration, baseDuration,startTime, commitTime)=>{
           console.log({id,phase, actualDuration, baseDuration,startTime, commitTime})
        }}>
          <OtherComponent/>
        </Profiler>
        <Profiler onRender={(id,phase, actualDuration, baseDuration,startTime, commitTime,interactions)=>{
           console.log({id,phase, actualDuration, baseDuration,startTime, commitTime, interactions})
        }} id="AnotherComponent" >
        <AnotherComponent />
        </Profiler> */}

<div className="form_ col-md-5 col-sm-12 offset-2">
                 <div className="form">
                     {/* <div className="alert alert-danger alert-dismissible">
                         Please enter all  fields
                     </div> */}
              <form   className="form">
                  
                <div className="form-group">
                    <label>
                        Phone number
                    </label>
                    <input value={phone}  onChange={(e)=>setPhone(e.target.value)} type="telephone" placeholder="Enter your phone number" className="form-control" name="phone_number"/>
                </div>
                <label>
                       Registeration type
                    </label>
                <div className="choose form-group">
                
               <input  onChange={(e)=>setRegisterType(e.target.value)} name="select"  type="radio" value="leader"/>Register as a leader
               <input onChange={(e)=>setRegisterType(e.target.value)}  name="select" type="radio" value="market"/>Register for E-market
                </div>
               
                
                <div className="form_select">
                   <label>
                        Select business category
                    </label>
                   <select value={category} onChange={(e)=>setCategory(e.target.value)} placeholder="select business category" id="select" name="category" className="form-control">
                      <option name="category" value="utensils">
                        Utensils
                      </option>
                      <option name="category" value="foodstuff">
                        Foodstuff
                      </option>
                      <option name="category" value="electronics">
                        Electronics
                      </option>
                      <option name="category" value="automobile">
                        Automobile
                      </option>
                      <option name="category" value="fashion">
                       Fashion
                      </option>
                      <option name="category" value="photography">
                        Photography
                      </option>
                      <option name="category" value="provision">
                        Provision
                      </option>
                      <option name="category" value="drugs">
                        Drugs
                      </option>
                   </select>
                </div>
              
                <div className="form-group">
                    <input onClick={()=>{
                        setStep(3)
                    }}  type="button" value="Next" className="form-control signup btn btn-primary"/>
                </div>
              </form>
           </div>
                     </div>  
                 

          {/* <OtherComponent />
          <AnotherComponent /> */}
          
          
        </section>
      </Suspense>
    </div>
  );
}
MyComponent.say = function() {
console.log("Found me")
}

function WrapComponent(component){
   class ImputedComponent extends React.Component {
       constructor(props){
        super(props);
        this.state={
             name:"Sunkanmi",
             Comp:null,
            
        }
       }
       static defaultProps={
           name:"Sunkanmi"
       }
       
       componentWillMount(){
           this.setState({Comp:component});
       }
       render(){
         if(this.state.Comp){
            return (
            <this.state.Comp  {...this.props} age={50}/>
            )
         }
         else{
           return <h1>Compnent is loading</h1>
         }

       }
       
    
      
       
   }
  ImputedComponent.say = component.say
   return ImputedComponent
}
console.log(typeof WrapComponent.say)
class CustomTextInput extends React.Component {
  constructor(props) {
    super(props);
    // create a ref to store the textInput DOM element
    this.textInput = React.createRef();
    this.focusTextInput = this.focusTextInput.bind(this);
  }

  focusTextInput() {
    // Explicitly focus the text input using the raw DOM API
    // Note: we're accessing "current" to get the DOM node
    this.textInput.current.focus();
  }

  render() {
    // tell React that we want to associate the <input> ref
    // with the `textInput` that we created in the constructor
    return (
      <div>
        <input
          type="text"
          ref={this.textInput} />
        <input
          type="button"
          value="Focus the text input"
          onClick={this.focusTextInput}
        />
      </div>
    );
  }
}
export default WrapComponent(MyComponent)
