import React , {Component , Consumer} from 'react';
import {ThemeContext} from '../contexts/ThemeContext';
  export default class Booklist extends Component{
   render(){
       
    <ThemeContext.Consumer>{(context)=>{
        const {isLightTheme, dark , light} = context;
        const Theme = isLightTheme ? light : dark
        return (
            <nav style={{background:Theme.bg}}>
            <ul style={{background:Theme.bg}}>
                <li  style={{color:Theme.syntax}}>New Hero</li>
                <li style={{color:Theme.syntax}}>Contact</li>
                <li style={{color:Theme.syntax}}>About</li>
                
            </ul>
  
          </nav>
        )
    }}
     
    </ThemeContext.Consumer>
    
       
       return(
        
        <div className="book-list">

          <ul>
              <li>New Hero</li>
              <li></li>
              <li>Man of Valour</li>
          </ul>

        </div>


       )
   }



  }
