import React, {useState} from 'react'
const user= localStorage.getItem('user');
const userJSON= JSON.parse(user)
const pro= userJSON._id
const Products = () => {
    const [products, setProducts]= useState([])
    fetch(`http://localhost:3003/getProducts/${pro}`, {
        method:'get',
        headers:{
         "content-type":'application/json'
        }
     }
     )
     .then((res)=>{
         return res.json()
     })
     .then((resJSON)=>{
    setProducts(resJSON)
     })
    return (
        <React.Fragment>
              <div className="col-10 col-md-6 mx-auto">
                  <h3 className="text-center text-slant">Your products</h3>
                {pro?
                <div className="row">
                    {
               userJSON.products.map((product)=>{
               return(
       
             <div className="col-10 col-md-4 mx-auto my-4">
            <div className="card product">
            <div className="card-title">
            <h5>{product.name}</h5>
            </div>
            <div className="card-body">
            {product.description}

            </div>
            <div className="price badge bg-primary">
            â‚¦{product.price}
            </div>

        </div>
      
    
            </div>
       
                             )
  
                         })
                    }
                   </div>:
                    <h2>You have not uploaded any product</h2>
                }
                
               </div>
               
            
         
        </React.Fragment>
    )
}

export default Products
