import React, {Component} from 'react';

import {graphql} from 'react-apollo';
import { addBookMutation} from '../queries/queries';

class addBook extends Component{
    constructor(props){
        super(props)
        this.state={
            name:"",
            genre:"",
            authorId:""
        }
    }
    authors= [
        {
          name: "ADEWUMI SUNKANMI",
          age: 20,
          id: "5d6d334831bc5010f8816348"
        },
        {
          name: "Benne hinn",
          age: 18,
          id: "5d6d33c331bc5010f881634a"
        },
        {
          name: "Andrew Wommack",
          age: 30,
          id: "5d6d344231bc5010f881634c"
        }
      ]
    displayAuthors(){
        var data= this.props.addBookMutation;
        
        if(data.loading){
            return(
                <option disabled>Loading authors.....</option>
            )   
        }
        else{
            return this.authors.map(author=>{
                
             return <option key={author.id} value={author.id}>{author.name}</option>
            })
        }
    }
    submitForm(e){
     e.preventDefault();
     this.props.addBookMutation({
         variables:{
             name:this.state.name,
             genre:this.state.genre,
             authorId:this.state.authorId
         }
     })
    }
    render(){
        
        return(
            <form onSubmit={this.submitForm.bind(this)} id='add-book'>
                <div className='field'>
                    <label>Name</label>
                    <input onChange={(e)=>{this.setState({name:e.target.value})}} type='text' placeholder='enter name'/>
                </div>

                <div className='field'>
                    <label>Genre</label>
                    <input  onChange={(e)=>{this.setState({genre:e.target.value})}} type='text' placeholder='enter genre'/>
                </div>
                <div className='field'>
                    <label>Author</label>
                   <select  onChange={(e)=>{this.setState({authorId:e.target.value})}}>
                       {this.displayAuthors()}
                   </select>
                </div>
                <div className='field'>
                   <button>+</button>
                </div>

            </form>
        )
    }
}


export default  graphql(addBookMutation,{name:"addBookMutation"})(addBook)
