const reducer= (state, action)=>{

    switch(action.type){
        case "ADD_BOOK":
            return  [...state, {
                title:action.book.title,
                author: action.book.author,
                id:Math.floor(Math.random()*100)
            }]

        case "REMOVE_BOOK":
            return  state.filter(()=> state.id !== action.id)
                   
    }
}
export default re
