import React,{Component , Consumer} from 'react';

import {Card , Col, Row, CardBody, CardGroup,CardTitle, CardSubtitle, Button} from 'reactstrap';
import Cart, { CartContext } from './CartContext';

class CartItem extends Component{
constructor(props){
    super(props);
    
}
render(){
  
    return(
       <CartContext.Consumer>
           {({cart})=>{
               return (
                <Row>
                   <ul>
                       {cart.map((item)=>{
                          return (
                             
                                  <Col sm={12} md={6}>

                                  </Col>
                              
                            
                          )
                       })}
                   </ul>
                   </Row>
                  
               )
           }}
       </CartContext.Consumer>
    )
}
}

export default CartItem;
