import React , {Component} from 'react';

import {gql} from 'apollo-boost';
import {graphql} from 'react-apollo';
i

const BookDetailsQuery = gql`
query($id:ID!){
   
    book(id:$id){
      id
      name
      genre
      author{
          name
          age
          books{
              name
              id
          }
      }

    }
}
`

class BookDetails extends Component{
  
 render(){
     console.log(this.props)
     return(
         <div>
           <h2>Book Details</h2>

         </div>
     )
 }
}

export default graphql(BookDetailsQuery, {
    name:"BookDetailsQuery",
    options:(props)=>{
        return{
            variables:{
                id:props.bookid
            }
        }
    }
})(BookDetails)
