import React , {Component} from 'react';

import {gql} from 'apollo-boost';
import {graphql} from 'react-apollo';


const BookDetailsQuery = gql`
query($id:ID!){
   
    book(id:$id){
      id
      name
      genre
      author{
          name
          age
          books{
              name
              id
          }
      }

    }
}
`

class BookDetails extends Component{
  displayDetails(){
      const book= this.props.BookDetailsQuery.book;
      if(book){
          return (
              <div className="bookdetails">
              <h1>{book.name}</h1>
              <p>{book.genre}</p>
              <p>{book.author}</p>
              <p>Other books by author</p>
              {/* <ul>
                 {book.author.books.map((item)=>{
                     <li key={item.id}>{item.name}</li>
                 })}
              </ul> */}
              </div>
          )
      }
  }
 render(){
    c
     return(
         <div>
           <h2>Book Details</h2>
            {this.displayDetails()}
         </div>
     )
 }
}

export default graphql(BookDetailsQuery, {
    name:"BookDetailsQuery",
    options:(props)=>{
        return{
            variables:{
                id:props.bookid
            }
        }
    }
})(BookDetails)
