import React,{useState} from 'react'


const UploadProduct = () => {
    const user= localStorage
    const [name, setName]= useState('');
    const [description, setDescription]= useState('');
    const [price, setPrice] = useState(null);
    const Submit= (e)=>{
        e.preventDefault();
        fetch("http://localhost:3003/upload", {
           method:'post',
           headers:{
            "content-type":'application/json'
           },
           body:JSON.stringify({
              name,
              description,
              price
           })
        }
        )
        .then((res)=>{
            return res.json()
        })
        .then((resJSON)=>{
             console.log(resJSON)
        })
        .catch(err=>{
            console.log(err)
        })
    }
    return (
        <React.Fragment>
            <div className="row">
            <div className='col-10 mx-auto my-3 col-md-6'>
                <h4 className="text-center text-capitalize mx-auto my-3">
                  Please fill the form below to add product
                </h4>
            <div className="form">
                <form onSubmit={Submit}>
                <div className="form-group">
                    <label htmlFor="input_fieled">Name of Product</label>
                    <input id="input_fieled" onChange={(e)=> setName(e.target.value)} className='form-control' placeholder="Enter the name of the product" />
                </div>
                <div className="form-group">
                    <label htmlFor="input_fieled">Description of Product</label>
                    <input id="input_fieled" onChange={(e)=> setDescription(e.target.value)} className='form-control' placeholder="Enter a short description  of the product" />
                </div>

                <div className="form-group">
                    <label htmlFor="input_fieled">Price of product</label>
                    <input onChange={(e)=> setPrice(e.target.value)} id="input_fieled" className='form-control' placeholder="Enter the price of the product" />
                </div>
                <input className="btn btn-primary" value="Add product" type="submit"/>
                </form>
               
            </div>
            </div>
            </div>
       
        </React.Fragment>
    )
}

export default UploadProduct
